-- #region documents table
CREATE TABLE IF NOT EXISTS "public"."documents" (
    "id" bigint PRIMARY KEY GENERATED BY DEFAULT AS IDENTITY,
    "content" text,
    "metadata" jsonb,
    "embedding" extensions.vector(768)
);
ALTER TABLE "public"."documents" OWNER TO "postgres";
-- #endregion

-- #region n8n_chat_histories table
CREATE TABLE IF NOT EXISTS "public"."n8n_chat_histories" (
    "id" integer PRIMARY KEY GENERATED BY DEFAULT AS IDENTITY,
    "session_id" character varying(255) NOT NULL,
    "message" jsonb NOT NULL
);
ALTER TABLE "public"."n8n_chat_histories" OWNER TO "postgres";
-- #endregion

-- #region push_subscriptions table
CREATE TABLE IF NOT EXISTS "public"."push_subscriptions" (
    "id" uuid PRIMARY KEY DEFAULT gen_random_uuid() NOT NULL,
    "user_id" uuid NOT NULL REFERENCES auth.users(id) ON DELETE CASCADE,
    "subscription" jsonb NOT NULL,
    "user_agent" text,
    "created_at" timestamp with time zone DEFAULT now() NOT NULL
);
ALTER TABLE "public"."push_subscriptions" OWNER TO "postgres";
CREATE INDEX IF NOT EXISTS "idx_push_subscriptions_user_id" ON "public"."push_subscriptions" USING btree ("user_id");
-- #endregion

-- #region user_secrets table
CREATE TABLE IF NOT EXISTS "public"."user_secrets" (
    "id" uuid PRIMARY KEY DEFAULT gen_random_uuid() NOT NULL,
    "user_id" uuid NOT NULL REFERENCES auth.users(id) ON DELETE CASCADE,
    "secret_name" text NOT NULL,
    "encrypted_value" text NOT NULL,
    "iv" text NOT NULL,
    "tag" text NOT NULL,
    "active" boolean DEFAULT true NOT NULL,
    "created_at" timestamp with time zone DEFAULT now() NOT NULL,
    "updated_at" timestamp with time zone DEFAULT now() NOT NULL,
    UNIQUE ("user_id", "secret_name")
);
ALTER TABLE "public"."user_secrets" OWNER TO "postgres";
CREATE INDEX IF NOT EXISTS "idx_user_secrets_user_id" ON "public"."user_secrets" USING btree ("user_id");
-- #endregion
